mapper.select:
	    SELECT 
	    FROM activity_discount 
	    WHERE disabled = 0


	    	AND name LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%') 


         	AND store_id = #{storeId,jdbcType=VARCHAR}



        	AND type = #{type,jdbcType=TINYINT}


		  	AND type IN 

		  		#{type,jdbcType=TINYINT}




		  	AND id IN 

		  		#{id,jdbcType=VARCHAR}




		  	AND status = #{status,jdbcType=TINYINT}



		  	AND start_time  >=  #{startTimeStart,jdbcType=TIMESTAMP}


		 	AND start_time  <=  #{startTimeEnd,jdbcType=TIMESTAMP}



		 	AND end_time  >=  #{endTimeStart,jdbcType=TIMESTAMP}


		  	AND end_time  <=  #{endTimeEnd,jdbcType=TIMESTAMP}

       ORDER BY update_time DESC,status ASC

SELECT id,CASE status WHEN 0 THEN 1 WHEN 1 THEN 2 END AS status
FROM activity_discount
WHERE 
	( 
		status = 0 
		AND start_time <= #{currentDate,jdbcType=TIMESTAMP} 
		AND end_time >= #{currentDate,jdbcType=TIMESTAMP}
	)
OR 
	(	
		status = 1 
		AND end_time < #{currentDate,jdbcType=TIMESTAMP}
	)

mapper.update:
	UPDATE activity_discount 
	SET status = #{status,jdbcType=INTEGER},
		update_time = #{updateTime,jdbcType=TIMESTAMP},
		update_user_id= #{updateUserId,jdbcType=VARCHAR} 
 	WHERE id in

   		#{activityId}

mapper.select:
SELECT COUNT(1)
FROM activity_discount t
LEFT JOIN activity_business_rel t1 ON t.id = t1.activity_id  
LEFT JOIN store_info t2 ON t1.business_id = t2.id AND t1.business_type = 2
WHERE t1.business_type IN (0,1,2)
AND t.status IN (0,1)
AND (
		 (t.start_time <= #{startTime,jdbcType=TIMESTAMP} AND t.end_time >= #{startTime,jdbcType=TIMESTAMP})
	  OR (t.start_time >= #{startTime,jdbcType=TIMESTAMP} AND t.start_time <= #{endTime,jdbcType=TIMESTAMP})
)
AND t.type = #{type,jdbcType=TINYINT}

	   AND ( t1.business_id IN 

	   		#{limitRangeId}


	   		OR t2.city_id IN 

	   			#{limitRangeId}


	   )


	   AND t.id != #{excludedId,jdbcType=VARCHAR}
