mapper.select:
select 
	s.id,
	s.name,
	s.status,
	s.start_time,
	s.end_time,
	s.create_time,
	s.create_user_id,
	s.update_time,
	s.update_user_id,
	(select count(*) from activity_label_goods where activity_id = s.id) as goodsCount
	from activity_label s where 1 = 1 

	and s.name like concat('%',#{name},'%' )


	and s.create_time >= #{createStartTime}


	and s.create_time <= #{createEndTime}


	and s.start_time >= #{activityStartStartTime}


	and s.start_time <= #{activityStartEndTime}


	and s.end_time >= #{activityEndStartTime}


	and s.end_time <= #{activityEndEndTime}


	and s.status = #{status}

order by s.create_time desc

mapper.update:
   	update activity_label set status = #{status} where id in 

 			#{item}  


		and (status = 0 or status = 1)

mapper.select:
select count(*) from activity_label c where 1 = 1
and (c.status = 1 or c.status = 0) AND ( #{startTime} BETWEEN c.start_time and c.end_time OR #{endTime} BETWEEN c.start_time and c.end_time OR (c.start_time >= #{startTime} and c.end_time <= #{endTime}) )
and c.id != #{id}
