mapper.select:
select aa.id, aa.activity_id, aa.content_id, aa.store_sku_id, aa.pic_url, aa.create_time, (case when bb.url is not null then bb.url when dd.url then dd.url end) goods_sku_pic, (case when ee.id is not null then ee.seckill_price else sku.online_price end) AS sku_price, sku.market_price, stock.sellable AS sellable_stock, stock.locked AS locked_stock from activity_h5_advert_content_goods aa LEFT JOIN goods_sku cc ON aa.store_sku_id = cc.id LEFT JOIN goods_sku_picture bb ON (aa.store_sku_id = bb.sku_id AND bb.type = 1) LEFT JOIN goods_store_sku sku on aa.store_sku_id = sku.id LEFT JOIN goods_store_sku_picture dd ON (aa.store_sku_id = dd.store_sku_id AND dd.type = 1) LEFT JOIN goods_store_sku_stock stock ON stock.store_sku_id = aa.store_sku_id LEFT JOIN activity_seckill ee ON (ee.store_sku_id = aa.store_sku_id AND seckill_status = 1) where 1=1
AND aa.activity_id = #{activityId,jdbcType=VARCHAR}
AND aa.content_id = #{contentId,jdbcType=VARCHAR}
GROUP BY aa.store_sku_id;

mapper.delete:
        delete from activity_h5_advert_content_goods
        where
        	1=1
        
	       	AND activity_id = #{activityId,jdbcType=VARCHAR}
        	 
        
	       	AND content_id = #{contentId,jdbcType=VARCHAR}
