mapper.update:
   	update activity_service_goods_recommend set status = #{status} where id in 

 			#{item}  


		and (status = 0 or status = 1)

mapper.select:
select count(*) from activity_service_goods_recommend c where 1 = 1
and (c.status = 1 or c.status = 0) AND ( #{startTime} BETWEEN c.start_time and c.end_time OR #{endTime} BETWEEN c.start_time and c.end_time OR (c.start_time >= #{startTime} and c.end_time <= #{endTime}) )
and c.id != #{id}
and ( c.area_type = 0 OR c.id in ( select a.activity_id from activity_service_goods_recommend_area a where a.activity_id = c.id AND a.area_id in
#{item}
) )
