mapper.select:
select

from column_app_recommend_goods

	
		AND recommend_id = #{recommendId}
	
	
		AND is_show = #{isShow}
	
	
		AND recommend_id IN
		
			#{item}
		
	

ORDER BY sort desc

SELECT goods.id, goods.recommend_id, goods.store_sku_id, goods.sort, goods.is_show FROM column_app_recommend_goods goods INNER JOIN column_app_recommend recommend ON recommend.id = goods.recommend_id WHERE recommend. STATUS = 0 AND recommend.disabled = 0 AND goods.is_show = 1
AND goods.store_sku_id IN
#{item}

mapper.insert:
INSERT INTO column_app_recommend_goods (
	id, recommend_id, store_sku_id, sort, is_show
) VALUES

	(
	#{item.id}, #{item.recommendId}, #{item.storeSkuId},
	#{item.sort}, #{item.isShow}
	)
