mapper.select:
		SELECT
		
		FROM column_app_recommend
		WHERE disabled = 0
		
			AND id != #{excludeId}
		
		
			AND title LIKE CONCAT('%',#{title},'%')
		
		
			AND place = #{place}
		
		
			AND area_type = #{areaType}
		
		
			AND create_time >= #{addStartTime}
		
		
			AND create_time <= #{addEndTime}
		
		
			AND update_time >= #{updateStartTime}
		
		
			AND update_time <= #{updateEndTime}
		
		
			AND status = #{status}
		
		  
              
               AND (
               		area_type = 0  
               		OR id IN
					
						#{item}
					
               )
            
               
               AND area_type = 0
               
              
               
					AND id IN
					
						#{item}
					
				
              
          
		
		  
              
               ORDER BY sort , update_time DESC
              
              
                ORDER BY status , update_time DESC
