mapper.select:
    	SELECT  
     	FROM skin_manager 
     	WHERE 1=1
	       
	       AND disabled = 0
	       
	       
	         AND activity_name LIKE CONCAT('%',#{activityName,jdbcType=VARCHAR},'%') 
	       
	       
	       
	         AND status = #{status,jdbcType=TINYINT}
	       
	       
	       
	         AND create_time  >=  #{createTimeStart,jdbcType=TIMESTAMP}
	       
	       
	         AND create_time  <=  #{createTimeEnd,jdbcType=TIMESTAMP}
	       
	       
	       
	         AND start_time  >=  #{startTime,jdbcType=TIMESTAMP}
	       
	       
	       
	         AND end_time  <=  #{endTime,jdbcType=TIMESTAMP}
	       
	     ORDER BY create_time DESC

mapper.update:
	UPDATE skin_manager
	SET disabled = 1,
    	update_time = #{updateTime,jdbcType=TIMESTAMP},
    	update_user_id = #{updateUserId,jdbcType=VARCHAR}
	WHERE id = #{id,jdbcType=VARCHAR}

	UPDATE skin_manager
	SET status = #{status,jdbcType=VARCHAR},
    	update_time = #{updateTime,jdbcType=TIMESTAMP},
    	update_user_id = #{updateUserId,jdbcType=VARCHAR}
	WHERE disabled = 0 and id = #{id,jdbcType=VARCHAR}

mapper.select:
	SELECT 	count(1)
 	FROM skin_manager 
 	
	 WHERE disabled = 0 and activity_name=#{activityName,jdbcType=VARCHAR}
