SELECT user.id, user.login_name, user.login_password, user.symmetry_password, user.salt,user.nick_name, user.real_name, user.birthday, user.marital_status, user.interest, user.gender, user.phone, user.email, user.status, user.sys_notice, user.prop_notice, user.phone_mac, user.pic_url, user.sign, user.description, user.create_time, user.update_time,user.profession, userext.point_val FROM sys_buyer_user user LEFT JOIN sys_buyer_ext userext on userext.user_id = user.id WHERE 1=1
AND user.login_name LIKE CONCAT('%',#{params.loginName},'%')
AND user.nick_name LIKE CONCAT('%',#{params.userName},'%')
AND user.create_time >= #{params.startCreateTime}
AND user.create_time <= #{params.endCreateTime}
AND userext.point_val >= #{params.startIntegral}
AND userext.point_val <= #{params.endIntegral}
ORDER BY CREATE_TIME DESC

    SELECT s.id,s.nick_name user_name,s.real_name,s.login_name,e.point_val
    FROM sys_buyer_user s left join sys_buyer_ext e on s.id=e.user_id WHERE 1=1


	         AND s.id = #{params.id}


	         AND s.nick_name = #{params.userName} 


	         AND s.login_name = #{params.loginName} 


        AND e.point_val >=${params.minPoint}


        AND e.point_val <=${params.maxPoint}


    order by s.create_time desc

    SELECT s.id,s.nick_name user_name,s.real_name,s.login_name

    ,e.point_val

    FROM sys_buyer_user s

    inner join sys_buyer_ext e on s.id=e.user_id

    WHERE 1=1


	         AND s.id = #{params.id}


	         AND s.nick_name = #{params.userName} 


	         AND s.login_name = #{params.loginName} 


        AND e.point_val >=${params.minPoint}


        AND e.point_val <=${params.maxPoint}


    order by s.create_time desc

SELECT phone
FROM sys_buyer_user 
WHERE id = #{id,jdbcType=VARCHAR}
